import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  SafeAreaView,
  TouchableOpacity,
  Image,
  Alert,
} from "react-native";
import api from "../../../service/api";
import leignelImagem from "../../../assets/leignel.jpg";
import ListaCategoria from "../../components/ListCategoria";
import AppIntroSlider from "react-native-app-intro-slider";
import {
  useFonts,
  Raleway_400Regular,
  Raleway_700Bold,
} from "@expo-google-fonts/raleway";

import leignel from "../../../assets/leignel.jpg";

const slides = [
  {
    key: "1",
    title: "Brunos Mars",
    text: "Treasure",
    backgroundColor: "#ffd23e",
    image: {
      uri: "https://jpimg.com.br/uploads/2021/03/bruno-mars.jpg",
    },
    button: {
      text: "Escute já",
    },
  },
  {
    key: "2",
    title: "Joji",
    text: "Sanctuary",
    backgroundColor: "#672612",
    image: {
      uri: "data:image/jpeg;base64,",
    },
    button: {
      text: "Escute já",
    },
  },
  {
    key: "3",
    title: "Adele",
    text: "SkyFall",
    backgroundColor: "#1d9ebe",
    image: {
      uri: "data:image/jpeg;base64,:image/jpeg;base64,",
    },
    button: {
      text: "Escute já",
    },
  },
];

const Home = ({ route }) => {
  const [musicas, setMusicas] = useState([]);
  const [fontsLoaded] = useFonts({
    Raleway_400Regular,
    Raleway_700Bold,
  });

  function renderSlider({ item }) {
    return (
      <View
        style={{
          backgroundColor: item.backgroundColor,
          margin: 10,
          marginTop: 30,
          padding: 20,
          paddingBottom: 0,
          borderRadius: 25,
          maxWidth: "95%",
          height: 210,
          position: "relative",
        }}
      >
        <View style={styles.sliderContainer}>
          <View style={styles.sliderContainerColImage}>
            <Image source={item.image} style={styles.profileImage} />
          </View>
          <View style={styles.sliderContainerCol}>
            <Text
              style={styles.cardTitle}
              numberOfLines={2}
              ellipsizeMode="tail"
            >
              {item.title}
            </Text>
            <Text
              style={styles.cardSubtitle}
              numberOfLines={2}
              ellipsizeMode="tail"
            >
              {item.text}
            </Text>
            <TouchableOpacity style={styles.listenButton}>
              <Text style={styles.listenButtonText}>Escute já</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    );
  }

  let loginUser;

  const getLogin = async (retryCount = 3) => {
    const login = AsyncStorage.getItem("@emailUser");
    loginUser = login;
  };

  const getMusicas = async (retryCount = 3) => {
    try {
      const response = await api.get();
      setMusicas(response.data);
    } catch (e) {
      if (e.response && e.response.status === 429 && retryCount > 0) {
        await new Promise((resolve) => setTimeout(resolve, 2000));
        await getMusicas(retryCount - 1);
      } else {
        console.log(e);
      }
    }
  };

  useEffect(() => {
    getMusicas();
  }, [musicas]);

  const fnLeignel = () => {
    Alert.alert("Melhor do que eu mereço!");
  };

  if (!fontsLoaded) {
    // Aguarde até que as fontes sejam carregadas
    return null;
  }
  return (
    <SafeAreaView style={styles.container}>
      <ScrollView>
        {/* Cabeçalho */}
        <View style={styles.header}></View>

        {/* Card central de apresentação */}
        {/* <View style={styles.blueCard}> */}
        <AppIntroSlider
          renderItem={renderSlider}
          data={slides}
          activeDotStyle={{
            backgroundColor: "#009cff",
          }}
        />
        {/* </View> */}

        {/* Sessão "Top Artistas" */}

        <View style={styles.topArtistsSection}>
          <Text style={styles.sectionTitle}>Top Artistas</Text>

          <View style={styles.artistImages}>
            <ScrollView horizontal showsHorizontalScrollIndicator={false}>
              <TouchableOpacity onPress={fnLeignel}>
                <Image source={leignel} style={styles.artistImage} />
              </TouchableOpacity>
              <Image
                source={{
                  uri: "data:image/jpeg;base64,",
                }}
                style={styles.artistImage}
              />
              <Image
                source={{
                  uri: "data:image/jpeg;base64,",
                }}
                style={styles.artistImage}
              />
              <Image
                source={{
                  uri: "data:image/jpeg;base64,",
                }}
                style={styles.artistImage}
              />
              <Image
                source={{
                  uri: "data:image/jpeg;base64,",
                }}
                style={styles.artistImage}
              />
              <Image
                source={{
                  uri: "data:image/jpeg;base64,",
                }}
                style={styles.artistImage}
              />
              <Image
                source={{
                  uri: "data:image/jpeg;base64,",
                }}
                style={styles.artistImage}
              />
              <Image
                source={{
                  uri: "data:image/jpeg;base64,",
                }}
                style={styles.artistImage}
              />
              <Image
                source={{
                  uri: "data:image/jpeg;base64,",
                }}
                style={styles.artistImage}
              />
            </ScrollView>
          </View>

          <Text style={styles.favorites}>Suas favoritas</Text>
        </View>
        {/* lista de Categorias */}
        <ListaCategoria musicas={musicas} setMusicas={setMusicas} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#141A35",
  },
  header: {
    flexDirection: "row",
    alignItems: "center",
    padding: 10,
    width: "100%",
  },
  link: {
    flex: 1,
    alignItems: "center",
  },
  linkText: {
    fontSize: 16,
    color: "blue",
    fontFamily: "Raleway_400Regular",
  },
  blueCard: {
    margin: 10,
    marginTop: 30,
    padding: 20,
    paddingBottom: 0,
    borderRadius: 25,
    width: "95%",
    height: 210,
    position: "relative",
  },
  sliderContainerCol: {
    display: "flex",
    alignSelf: "center",
    flexDirection: "column",
    width: "80%",
  },
  sliderContainerColImage: {
    display: "flex",
    alignSelf: "center",
    flexDirection: "column",
    width: "40%",
  },
  sliderContainer: {
    display: "flex",
    flexDirection: "row",
    position: "relative",
    gap: 26,
  },
  cardContent: {
    flexDirection: "row",
    alignItems: "center",
  },
  profileImage: {
    width: 110,
    height: 110,
    borderRadius: 50,
  },
  textContainer: {
    flex: 1,
  },
  cardTitle: {
    color: "white",
    fontSize: 20,
    fontFamily: "Raleway_700Bold",
    flexShrink: 1,
    flexWrap: "wrap",
  },
  cardSubtitle: {
    color: "white",
    fontSize: 16,
    fontFamily: "Raleway_400Regular",
  },

  blueCardText: {
    color: "white",
    fontSize: 18,
  },
  topArtistsSection: {
    marginTop: 24,
    alignItems: "center",
    marginBottom: 30,
  },
  sectionTitle: {
    fontSize: 20,
    fontFamily: "Raleway_700Bold",
    color: "#ECECEC",
  },
  artistImages: {
    flexDirection: "row",
    marginTop: 30,
  },
  artistImage: {
    width: 80,
    height: 80,
    borderRadius: 40,
    marginHorizontal: 10,
  },
  listenButton: {
    backgroundColor: "white",
    borderRadius: 10,
    paddingVertical: 6,
    paddingHorizontal: 10,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 10,
    width: "50%",
  },
  listenButtonText: {
    color: "blue",
    fontSize: 16,
    fontFamily: "Raleway_400Regular",
  },

  favorites: {
    color: "white",
    fontSize: 20,
    fontFamily: "Raleway_700Bold",
    textAlign: "center",
    justifyContent: "center",
    marginTop: 20,
  },
});

export default Home;
